result:
@r: 100;
* {
  padding: 0;
  margin: 0;
}
html,
body {
  height: 100%;
  overflow: hidden;
  .my-rate {
    max-width: 750px;
    margin: 0 auto;
  }
}
.smd-btn-cls {
  width: 686rem / @r;
  height: 100rem / @r;
  border-radius: 8rem / @r;
  font-size: 36rem / @r;
  color: #fff;
  line-height: 100rem / @r;
  text-align: center;
  margin: 0 auto;
  background: #4d7bfe;
}
.submit {
  width: 686rem / @r;
  height: 100rem / @r;
  background-color: #4d7bfe;
  border-radius: 8rem / @r;
  font-size: 36rem / @r;
  color: #fff;
  line-height: 100rem / @r;
  text-align: center;
  margin: 60rem / @r auto 0;
}
.smd-floating-layer {
  position: fixed;
  width: 100%;
  height: 100%;
  z-index: 1000;
  background-color: rgba(0, 0, 0, 0.8);
  top: 0;
  max-width: 750px;
  margin: 0 auto;
}
.footer-content-t {
  transition: transform 0.5s;
  transform: translate(0, 0);
}
.footer-content-o {
  transition: transform 0.5s;
  transform: translate(0, 100%) translateZ(0);
}
.footer-content {
  width: 100%;
  height: 641rem / @r;
  background-color: #fff;
  position: fixed;
  bottom: 0;
  max-width: 750px;
  border-radius: 10rem / @r 10rem / @r 0px 0px;
  z-index: 1500;
  .footer-title {
    font-size: 40rem / @r;
    height: 136rem / @r;
    line-height: 136rem / @r;
    color: #333;
    font-weight: 600;
    position: relative;
    border-bottom: 1px solid #eeeeee;
    .footer-close {
      width: 60rem / @r;
      height: 60rem / @r;
      position: absolute;
      right: 30rem / @r;
      top: 50%;
      box-sizing: border-box;
      transform: translateY(-50%);
    }
    .footer-close::before {
      content: '';
      width: 32rem / @r;
      height: 32rem / @r;
      position: absolute;
      left: 50%;
      top: 50%;
      transform: translate(-50%, -50%);
      margin-top: -9rem / @r;
      margin-left: -9rem / @r;
      background-image: url(data:image/png;base64,);
      background-repeat: no-repeat;
      -webkit-background-size: cover;
      background-size: cover;
    }
  }
}
.score-msg {
  font-size: 32rem / @r;
  color: #666666;
  letter-spacing: 0;
  text-align: center;
}
